//---WEBHOOK SERVER -------------------
/*
	Make sure you run your bot with node bot.js the first time you use this
	so the dependencies can install!

	Access to forward ports on your modem/router/isp is required. 

	This cannot be used on a server behind a NAT, non static IP address.
*/

//------------ SETTINGS -----------------------------------

// ip of machine/public ip if no Lan
const _HOSTNAME = "http://127.0.0.1"; 

// this port needs to be forwarded
const _PORT = "8080";

// random numbers and letters
// at least as long as these --------------
const _ID = "hnjighdfHFGHFg78dsgddshgfdhfg3"; 

// random numbers and letters
// at least as long as these -----------------------------
const _TOKEN = "98JHfgdhkjsdfg8778954645hbgjmdfgdJJesjhfdo"; 

// channel id of the channel you want the hook to print to
const _CHANNEL_ID = "123456789101112"; 

// ---------- END SETTINGS -----------------------------



//------------ONLY CHANGE BELOW IF YOU KNOW WHATS UP! -----

// check and install dependencies with wrexmods
const express    = this.getWrexMods().require('express');
const bodyParser = this.getWrexMods().require('body-parser');
const cors       = this.getWrexMods().require('cors');

const app = express();
app.use(bodyParser.json());
app.use(bodyParser.text());
app.use(bodyParser.urlencoded({extended: true}));
app.use(cors());

const channel = client.channels.get(_CHANNEL_ID);


// construct the end point
app.post('/' + _ID + '/' + _TOKEN  +'/hook', function (req, res) {

    try {
        if (!req.body) {
            return res.status(400).send('Bad Request');
		}

		if(server && channel){                                  
			let canSendMessage = channel.permissionsFor(client.user).has("SEND_MESSAGES");

			if(canSendMessage){
				channel.send(req.body);   
			}                              																					                                                             
		}

        res.status(200).send({message: "Webhook Sent!", status: "OK", error: false });
    } catch (error) {
        res.status(200).send({message: error, status: "ERROR", error: true });
    }  
});

app.listen(_PORT || 443, function () {
    console.log('[WEBHOOKS] Service Running at ' + _HOSTNAME + ":" + _PORT + '/' + _ID + '/' + _TOKEN  +'/hook');
});
